# Modified from bcbio-rnaseq qc-summary tempalte
# https://github.com/roryk/bcbio.rnaseq/blob/master/resources/bcbio/qc-summary.template


#' Import sailfish counts
#'
#' Import \code{bcbio-rnaseq} \code{sailfish} count data run using
#' \code{tximport}
#'
#' @author Michael Steinbaugh
#'
#' @import readr
#' @import tximport
#' @importFrom utils read.csv
#'
#' @param bcbio bcbio run object
#' @param summary Summary data frame
#'
#' @return txi \code{tximport} count data generated by \code{sailfish}
#' @export
#'
#' @examples
#' \dontrun{
#' import_sailfish(bcbio, summary)
#' }
import_sailfish <- function(bcbio, summary) {
    if (!is.list(bcbio)) {
        stop("bcbio run object is required.")
    }
    if (!is.data.frame(summary)) {
        stop("Summary data frame is required.")
    }
    if (!file.exists(file.path(bcbio$project_dir, "tx2gene.csv"))) {
        stop("tx2gene.csv file not found.")
    }

    tx2gene <- file.path(bcbio$project_dir, "tx2gene.csv") %>%
        utils::read.csv(header = FALSE)

    # Parse the HPC sailfish directories
    sailfish_files <- dir(bcbio$final_dir) %>%
        .[. %in% summary$description] %>%
        file.path(bcbio$final_dir,
                  .,
                  "sailfish",
                  "quant",
                  "quant.sf") %>%
        sort
    if (!length(sailfish_files)) {
        stop("No sailfish files were found.")
    }

    names(sailfish_files) <- sort(summary$description)

    # Import the counts
    txi <- tximport::tximport(sailfish_files,
                              type = "sailfish",
                              tx2gene = tx2gene,
                              reader = readr::read_tsv,
                              countsFromAbundance = "lengthScaledTPM")
    save(txi, file = "data/txi.rda")

    return(txi)
}
